mapper.insert:
INSERT INTO TB_BOARD (
    BOARD_SEQ
    , BOARD_TITLE
    , BOARD_CONTENT
    , created_DATE
    , BOARD_VIEWS
    , CATEGORY
    , FILENAME
    , MEMBER_SEQ
) VALUES (
     NEXTVAL('BOARD_SEQ')
    , #{boardTitle}
    , #{boardContent}
    , CURRENT_TIMESTAMP
    , #{boardViews}
    , #{category}
    , #{filename}
    , #{memberSeq}
)

mapper.update:
UPDATE TB_BOARD
SET BOARD_VIEWS = BOARD_VIEWS + 1
WHERE BOARD_SEQ = #{boardSeq}
