mapper.insert:
INSERT INTO TB_MEMBER ( MEMBER_SEQ
, MEMBER_ID
, MEMBER_NICKNAME
, MEMBER_FILENAME
, MEMBER_NAME
, MEMBER_PWD
) VALUES (
NEXTVAL('MEMBER_SEQ')
, #{memberId}
, #{memberNickname}
, #{memberFilename}
, #{memberName}
, #{memberPwd})

mapper.update:
UPDATE TB_MEMBER
SET MEMBER_NICKNAME = #{newNickname}
WHERE MEMBER_SEQ = #{memberSeq}

UPDATE TB_MEMBER
SET MEMBER_FILENAME = #{fileName}
HERE MEMBER_SEQ = #{memberSeq}
